import { setTimeout, clearTimeout, setInterval, clearInterval, setImmediate, clearImmediate } from './timers.js';
import { TextEncoder } from './text-encoder.js';
import { Headers } from './headers.js';
import { fetch, Request, Response } from './fetch.js';

// Also place on globalThis

globalThis.setTimeout = setTimeout;
globalThis.clearTimeout = clearTimeout;
globalThis.setInterval = setInterval;
globalThis.clearInterval = clearInterval;
globalThis.setImmediate = setImmediate;
globalThis.clearImmediate = clearImmediate;
globalThis.fetch = fetch;
globalThis.Headers = Headers;
globalThis.Request = Request;
globalThis.Response = Response;
globalThis.TextEncoder = TextEncoder;

// This helps with libraries like underscore that don't know about globalThis yet.
globalThis.window = globalThis;
